import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";
import FileBase64 from "react-file-base64";

export default function Edit() {
  const [form, setForm] = useState({
    item: "",
    name: "",
    objectclass: "",
    image: "",
    description: "",
    containment: "",

    records: [],
  });
  const params = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchData() {
      const id = params.id.toString();
      const response = await fetch(
        `http://localhost:5000/record/${params.id.toString()}`
      );

      if (!response.ok) {
        const message = `An error has occurred: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const record = await response.json();
      if (!record) {
        window.alert(`Record with id ${id} not found`);
        navigate("/");
        return;
      }

      setForm(record);
    }

    fetchData();

    return;
  }, [params.id, navigate]);

  // These methods will update the state properties.
  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  async function onSubmit(e) {
    e.preventDefault();
    const editedPerson = {
      item: form.item,
      name: form.name,
      objectclass: form.objectclass,
      image: form.image,
      description: form.description,
      containment: form.containment,
    };

    // This will send a post request to update the data in the database.
    await fetch(`http://localhost:5000/update/${params.id}`, {
      method: "POST",
      body: JSON.stringify(editedPerson),
      headers: {
        "Content-Type": "application/json",
      },
    });

    navigate("/");
  }

  // This following section will display the form that takes input from the user to update the data.
  return (
    <div>
      <img src={scp.image} className="card-img-top p-1" alt="scp" />
      <hr />
      <div className="card-body p-2">
        <h5 className="card-title">Item: {scp.item}</h5>
        <p className="card-text">Objectclass: {scp.objectclass}</p>
        <hr />
        <p className="card-text">Desription: {scp.description}</p>
        <hr />
        <p className="card-text">Containment: {scp.containment}</p>
        <hr />
        <p className="card-text">References: {scp.referances}</p>
        <hr />
        <p className="card-text">Additional Notes: {scp.misc}</p>
      </div>
    </div>
  );
}
